function cambiaformulario(tipo)
{

var path="";   
switch (tipo) {
   
    
    case 1:
        $("#nombreseccion").attr("placeholder","Nombre de la Industria");
        
      path="assets/contactanos.industria.data.php";
        break;
    case 2:
        $("#nombreseccion").attr("placeholder","Nombre de la Instituci贸n");
      path="assets/contactanos.gobierno.data.php";
        
        break;
    case 3:
        $("#nombreseccion").attr("placeholder","Ciudad");
      path="assets/contactanos.ciudadania.data.php";
       break;
}     

$.getJSON(path, function(j){
  var options = '';
for (var i = 0; i < j.length; i++) {
    if(i==0)
    {
        options += '<option value="">' + j[i] + '</option>';
    }
    else
    {
        options += '<option value="' + j[i]+ '">' + j[i] + '</option>';
    }
        
      }
$("select#tipo").html(options);      
});



}

